'use strict';

var fs = require('fs');

module.exports = {
  deserializeJsonFile: function deserializeJsonFile(filePath) {
    return new Promise(function (resolve, reject) {
      fs.readFile(filePath, 'utf8', function (error, content) {
        if (error) {
          return reject(error);
        }

        try {
          var result = JSON.parse(content);
          resolve(result);
        } catch (err) {
          reject(err);
        }
      });
    });
  }
};