'use strict';

var fs = require('fs');

module.exports = {
  serializeJsonFile: function serializeJsonFile(filePath, object) {
    return new Promise(function (resolve, reject) {
      var serializedObject = JSON.stringify(object, null, '  ');
      fs.writeFile(filePath, serializedObject, 'utf-8', function (error) {
        error ? reject(error) : resolve();
      });
    });
  }
};